import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const { email, password } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', formData);
      localStorage.setItem('token', response.data.token); // Save JWT token in localStorage
      alert('Login successful');
    } catch (err) {
      alert('Error: ' + err.response.data.message);
    }
  };

  return (
    <div className="max-w-md mx-auto my-8 p-6 bg-gray-800 text-white rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6">Log In</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <input
            type="email"
            name="email"
            value={email}
            onChange={handleChange}
            placeholder="Email"
            className="w-full p-3 bg-gray-700 text-white rounded-md"
          />
        </div>
        <div className="mb-6">
          <input
            type="password"
            name="password"
            value={password}
            onChange={handleChange}
            placeholder="Password"
            className="w-full p-3 bg-gray-700 text-white rounded-md"
          />
        </div>
        <button type="submit" className="w-full p-3 bg-teal-500 text-white rounded-md hover:bg-teal-600">
          Log In
        </button>
      </form>
    </div>
  );
};

export default Login;
