import React from 'react';
import { FaDownload } from 'react-icons/fa';
import { Box, Button, Typography, Container } from '@mui/material';

const Home = () => {
  return (
    <Box
      sx={{
        height: '100vh',
        background: 'linear-gradient(135deg, #6e7e85, #a8c0d8)', // Gradient background
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        padding: '20px',
      }}
    >
      <Container>
        {/* Profile Image */}
        <Box sx={{ mb: 6 }}>
          <img
            src="/images/profile.jpg"
            alt="Profile"
            className="w-40 h-40 object-cover rounded-full mx-auto border-4 border-teal-500 shadow-lg"
          />
        </Box>

        {/* Introduction Text */}
        <Typography variant="h3" sx={{ color: 'white', mb: 2 }}>
          Hi, I'm <span className="text-teal-400">K Manjunath</span>
        </Typography>
        <Typography variant="h6" sx={{ color: 'white', mb: 4 }}>
          A passionate Developer crafting digital experiences.
        </Typography>

        {/* Buttons */}
        <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center', gap: 3 }}>
          {/* Download CV Button */}
          <Button
            variant="contained"
            color="primary"
            href="/my-cv.pdf"
            download
            sx={{
              padding: '10px 20px',
              backgroundColor: '#00695f',
              '&:hover': { backgroundColor: '#004d40' },
            }}
            startIcon={<FaDownload />}
          >
            Download CV
          </Button>

          {/* View Projects Button */}
          <Button
            variant="contained"
            color="secondary"
            href="#projects"
            sx={{
              padding: '10px 20px',
              backgroundColor: '#004d40',
              '&:hover': { backgroundColor: '#00332e' },
            }}
          >
            View Projects
          </Button>

          {/* View Certificates Button */}
          <Button
            variant="contained"
            color="info"
            href="#certificates"
            sx={{
              padding: '10px 20px',
              backgroundColor: '#283593',
              '&:hover': { backgroundColor: '#1a237e' },
            }}
          >
            View Certificates
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default Home;
