import React from 'react';
import { FaDownload } from 'react-icons/fa';

const Home = () => {
  return (
    <section id="home" className="h-screen flex items-center justify-center bg-gradient-to-br from-gray-800 to-gray-900 text-white px-6">
      <div className="text-center">
        <h2 className="text-4xl md:text-6xl font-bold mb-4">Hi, I'm <span className="text-teal-400">K Manjunath</span></h2>
        <p className="text-lg md:text-xl mb-6">A passionate Developer crafting digital experiences.</p>
        <a
            href="/my-cv.pdf"
            download
             className="inline-flex items-center gap-2 mt-6 px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition duration-300"
        >
            <FaDownload /> Download CV
        </a>
        <a
          href="#projects"
          className="bg-teal-500 hover:bg-teal-600 text-white font-semibold py-2 px-4 rounded shadow-lg transition duration-300"
        >
          View Projects
        </a>
      </div>
    </section>
  );
};

export default Home;
